import React, { useState } from 'react';

const SignUpForm = () => {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [company, setCompany] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const placeholders = {
        'First Name': 'Enter your first name',
        'Last Name': 'Enter your last name',
        'Email': 'Enter your email',
        'Company': 'Enter your company name',
        'Password': 'Enter your password',
        'Confirm Password': 'Confirm your password'
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        setErrorMessage('');

        // Validate password match
        if (password !== confirmPassword) {
            setErrorMessage('Passwords do not match.');
            return;
        }

        // Proceed with form submission (e.g., API call)
        console.log('Form submitted:', { firstName, lastName, email, company, password });

    };

    return (
        <form onSubmit={handleSubmit} className="grid grid-cols-2 gap-x-8 gap-y-4">
            <h2 className="col-span-2 text-white font-montserrat text-2xl mb-6 text-center">
                Create Your Account
            </h2>

            {/* Input fields */}
            {['First Name', 'Last Name', 'Email', 'Company', 'Password', 'Confirm Password'].map((field, index) => {
                let value;
                let onChange;

                switch (field) {
                    case 'First Name':
                        value = firstName;
                        onChange = (e) => setFirstName(e.target.value);
                        break;
                    case 'Last Name':
                        value = lastName;
                        onChange = (e) => setLastName(e.target.value);
                        break;
                    case 'Email':
                        value = email;
                        onChange = (e) => setEmail(e.target.value);
                        break;
                    case 'Company':
                        value = company;
                        onChange = (e) => setCompany(e.target.value);
                        break;
                    case 'Password':
                        value = password;
                        onChange = (e) => setPassword(e.target.value);
                        break;
                    case 'Confirm Password':
                        value = confirmPassword;
                        onChange = (e) => setConfirmPassword(e.target.value);
                        break;
                    default:
                        break;
                }

                return (
                    <div className="mb-4" key={index}>
                        <label className="block text-white font-montserrat mb-2">{field}</label>
                        <input
                            type={field.includes('Password') ? 'password' : 'text'}
                            placeholder={placeholders[field]}
                            value={value}
                            onChange={onChange}
                            className="w-full p-3 rounded-md bg-[#150824] border border-[#a8acb3] text-white focus:outline-none focus:ring-2 focus:ring-[#ff5002]"
                        />
                    </div>
                );
            })}

            {/* Error Message */}
            {errorMessage && <div className="col-span-2 text-red-500 text-center mb-4">{errorMessage}</div>}

            {/* Sign Up Button */}
            <button
                type="submit"
                className="col-span-2 bg-[#ff5002] text-white font-montserrat font-bold px-4 py-2 rounded-md hover:bg-[#ff6f00] transition"
            >
                Sign Up
            </button>
        </form>
    );
}

export default SignUpForm;
