import { useState } from 'react';
import { motion } from 'framer-motion';
import Header from './Header';
import LoginForm from './LoginForm';
import SignUpForm from './SignUpForm'; // Import the SignUpForm

const HomePage = () => {
    const [isLoginVisible, setIsLoginVisible] = useState(false);
    const [isSignUpVisible, setIsSignUpVisible] = useState(false); // State for sign-up visibility
    const [isSlidingOut, setIsSlidingOut] = useState(false);

    const handleLoginClick = () => {
        setIsSlidingOut(true);
        setTimeout(() => {
            setIsLoginVisible(true);
            setIsSignUpVisible(false); // Ensure sign-up is hidden
        }, 500);
    };

    const handleBackHomeClick = () => {
        setIsLoginVisible(false);
        setIsSignUpVisible(false); // Hide sign-up when going back
        setIsSlidingOut(false);
    };

    const handleSignUpClick = () => {
        setIsSlidingOut(true);
        setTimeout(() => {
            setIsSignUpVisible(true); // Show sign-up form
            setIsLoginVisible(false); // Ensure login is hidden
        }, 500);
    };

    return (
        <div className="min-h-screen max-h-screen bg-gradient-to-b from-[#ff5002] to-[#150824] p-4 flex flex-col justify-center">
            <Header onLoginClick={handleLoginClick} onBackHomeClick={handleBackHomeClick} />

            <motion.div
                initial={{ x: 0, opacity: 1 }}
                animate={isSlidingOut ? { x: '-100%', opacity: 0 } : { x: 0, opacity: 1 }}
                exit={{ x: '100%', opacity: 0 }}
                transition={{ duration: 0.5 }}
            >
                <section className={`flex justify-center ${isLoginVisible || isSignUpVisible ? 'hidden' : 'h-[70vh]'}`}>
                    <div className="content-container bg-[#150824] bg-opacity-40 p-8 rounded-lg w-[80vw] max-w-7xl">
                        <div className="button-container p-8 rounded-lg max-w-4xl flex flex-col items-center justify-center mx-auto absolute left-1/2 transform -translate-x-1/2 bottom-1/3">
                            <h3 className="font-montserrat text-white text-5xl text-center mb-4 py-20">Available Tools</h3>
                            {/* Available tools buttons go here */}
                        </div>
                    </div>
                </section>
            </motion.div>

            {isLoginVisible && (
                <motion.div
                    initial={{ x: '100%', opacity: 0 }}
                    animate={{ x: 0, opacity: 1 }}
                    exit={{ x: '100%', opacity: 0 }}
                    transition={{ duration: 0.5 }}
                >
                    <section className="flex justify-center h-[70vh]">
                        <div className="content-container fixed top-1/2 left-1/2 w-[80vw] h-full bg-[#150824] bg-opacity-40 p-8 rounded-lg flex items-center justify-center transform -translate-x-1/2 -translate-y-1/2">
                            <LoginForm onSignUpClick={handleSignUpClick} />
                        </div>
                    </section>
                </motion.div>
            )}

            {isSignUpVisible && (
                <motion.div
                    initial={{ x: '100%', opacity: 0 }}
                    animate={{ x: 0, opacity: 1 }}
                    exit={{ x: '100%', opacity: 0 }}
                    transition={{ duration: 0.5 }}
                >
                    <section className="flex justify-center h-[70vh]">
                        <div className="content-container fixed top-1/2 left-1/2 w-[80vw] h-full bg-[#150824] bg-opacity-40 p-8 rounded-lg flex items-center justify-center transform -translate-x-1/2 -translate-y-1/2">
                            <SignUpForm />
                        </div>
                    </section>
                </motion.div>
            )}
        </div>
    );
};

export default HomePage;
