import React from 'react';

const SignUpForm = () => {
    const handleSubmit = (e) => {
        e.preventDefault();
        // Handle the sign-up logic here (e.g., API call)
        console.log("Sign Up Submitted");
    };

    return (
        <form className="flex flex-col h-auto" onSubmit={handleSubmit}>
            {/* Header */}
            <h2 className="text-white font-montserrat text-2xl mb-6 text-center">
                Create an Account
            </h2>

            {/* Name Input */}
            <div className="mb-4">
                <label className="block text-white font-montserrat mb-2">Name</label>
                <input
                    type="text"
                    placeholder="Enter your name"
                    className="w-full p-3 rounded-md bg-[#150824] border border-[#a8acb3] text-white focus:outline-none focus:ring-2 focus:ring-[#ff5002]"
                />
            </div>

            {/* Email Input */}
            <div className="mb-4">
                <label className="block text-white font-montserrat mb-2">Email</label>
                <input
                    type="email"
                    placeholder="Enter your email"
                    className="w-full p-3 rounded-md bg-[#150824] border border-[#a8acb3] text-white focus:outline-none focus:ring-2 focus:ring-[#ff5002]"
                />
            </div>

            {/* Password Input */}
            <div className="mb-4">
                <label className="block text-white font-montserrat mb-2">Password</label>
                <input
                    type="password"
                    placeholder="Enter your password"
                    className="w-full p-3 rounded-md bg-[#150824] border border-[#a8acb3] text-white focus:outline-none focus:ring-2 focus:ring-[#ff5002]"
                />
            </div>

            {/* Sign Up Button */}
            <button
                type="submit"
                className="bg-[#ff5002] text-white font-montserrat font-bold px-4 py-2 rounded-md hover:bg-[#ff6f00] transition mb-4"
            >
                Sign Up
            </button>
        </form>
    );
}

export default SignUpForm;
