import { Link } from 'react-router-dom';
import { useState } from 'react';
import { motion } from 'framer-motion';

const Header = ({ onLoginClick }) => (
    <header className="bg-[rgb(21,8,36)] fixed top-2 left-2 right-2 flex justify-between items-center p-4 rounded-b-lg rounded-t-lg z-10">
        <div className='flex items-center space-x-4 bg-[#150824] hover:bg-[#a8acb3] hover:bg-opacity-50 transition border-300 p-2 rounded-md'>
            <img src="https://pdf-background-images.s3.us-east-2.amazonaws.com/logo.png" alt="Logo" className="h-10" />
            <span className='text-white font-montserrat text-lg'>Onboarding Tools</span>
        </div>
        <div className="flex space-x-4">
            <Link to="/signup">
                <Button label="Create Account" />
            </Link>
            <button onClick={onLoginClick} className="bg-white bg-opacity-80 text-[#150824] font-montserrat font-bold px-5 py-3 rounded-md hover:bg-opacity-100 transition">
                Log In
            </button>
        </div>
    </header>
);

const Button = ({ label }) => (
    <button className="bg-white bg-opacity-80 text-[#150824] font-montserrat font-bold px-5 py-3 rounded-md hover:bg-opacity-100 transition">
        {label}
    </button>
);

const ToolButton = ({ label, to }) => (
    <Link to={to}>
        <button className="bg-white bg-opacity-85 rounded-lg shadow-md p-4 flex items-center justify-center hover:bg-opacity-100 transition h-20">
            {label}
        </button>
    </Link>
);

const AvailableTools = () => (
    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-3 gap-4">
        <ToolButton label="US Data Pack Upload" to="/us-data-pack" />
        <ToolButton label="Prep Forms for Uptick" to="/form-prep" />
        <ToolButton label="Map Data from Service Trade" to="/service-trade-export" />
    </div>
);

const HomePage = () => {
    const [isSliding, setIsSliding] = useState(false);

    const handleLoginClick = () => {
        setIsSliding(true);
        setTimeout(() => {
            // Redirect logic here (replace with your navigation logic)
            window.location.href = '/login'; // or use useNavigate if in a Router context
        }, 500); // Match this delay with the animation duration
    };

    return (
        <div className="min-h-screen bg-gradient-to-b from-[#ff5002] to-[#150824] p-4 flex flex-col justify-center">
            <Header onLoginClick={handleLoginClick} />
            <motion.div
                initial={{ x: 0 }}
                animate={isSliding ? { x: '-100%', opacity: 0 } : { x: 0, opacity: 1 }}
                exit={{ x: '100%', opacity: 0 }} // Exit animation to the right
                transition={{ duration: 0.5 }}
            >
                <section className="flex justify-center h-[70vh]">
                    <div className="content-container bg-[#150824] bg-opacity-40 p-8 rounded-lg w-[100vw] h-full max-w-7xl">
                        <div className="button-container p-8 rounded-lg max-w-4xl flex flex-col items-center justify-center mx-auto absolute left-1/2 transform -translate-x-1/2 bottom-1/3">
                            <h3 className="font-montserrat text-white text-5xl text-center mb-4 py-20">Available Tools</h3>
                            <AvailableTools />
                        </div>
                    </div>
                </section>
            </motion.div>
        </div>
    );
}

export default HomePage;
