import { Link } from 'react-router-dom';

const Header = () => (
    <header className="bg-[rgb(21,8,36)] fixed top-2 left-2 right-2 flex justify-between items-center p-4 rounded-b-lg rounded-t-lg z-10">
        <Link to='/home' className='flex items-center space-x-4 bg-[#150824] hover:bg-[#a8acb3] hover:bg-opacity-50 transition border-300 p-2 rounded-md'>
            <img src="https://pdf-background-images.s3.us-east-2.amazonaws.com/logo.png" alt="Logo" className="h-10" />
            <span className='text-white font-montserrat text-lg'>Onboarding Tools</span>
        </Link>
        <div className="flex space-x-4">
            <Link to="/signup">
                <button className="bg-white bg-opacity-80 text-[#150824] font-montserrat font-bold px-5 py-3 rounded-md hover:bg-opacity-100 transition">
                    Create Account
                </button>
            </Link>
        </div>
    </header>
);

const InputField = ({ label, type, placeholder }) => (
    <div className="mb-4">
        <label className="block text-white font-montserrat mb-2">{label}</label>
        <input
            type={type}
            placeholder={placeholder}
            className="w-full p-3 rounded-md bg-[#150824] border border-[#a8acb3] text-white focus:outline-none focus:ring-2 focus:ring-[#ff5002] transition"
        />
    </div>
);

const LoginForm = () => (
    <form className="bg-[#150824] bg-opacity-40 p-8 rounded-lg shadow-md">
        <h3 className="font-montserrat text-white text-3xl text-center mb-6">Log In</h3>
        <InputField label="Email" type="email" placeholder="Enter your email" />
        <InputField label="Password" type="password" placeholder="Enter your password" />
        <button
            type="submit"
            className="w-full bg-white bg-opacity-80 text-[#150824] font-montserrat font-bold px-5 py-3 rounded-md hover:bg-opacity-100 transition"
        >
            Log In
        </button>
    </form>
);

const LoginPage = () => {
    return (
        <div className="min-h-screen bg-gradient-to-b from-[#ff5002] to-[#150824] p-4 flex flex-col justify-center">
            <Header />
            <section className="flex flex-grow justify-center items-center min-w-[600px]]">
            <div className="content-container bg-[#150824] bg-opacity-40 p-10 rounded-lg w-[600px]"> 
                    <LoginForm />
                </div>
            </section>
        </div>
    );
}

export default LoginPage;
